export function urlParams(){return new URLSearchParams(location.search)}export function hasParam(t){return urlParams.has(t)}export async function fetchText(t,e){await fetch(t).then((t=>t.text())).then((t=>{e(t)}))}export async function fetchJSON(t,e){await fetch(t).then((t=>t.json())).then((t=>{e(t)}))}export function trigger(t,e){let n=new Event(e);t.dispatchEvent(n)}export function shuffleArray(t){return t.sort((()=>Math.random()-.5))}